    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        DefaultTableModel model1=(DefaultTableModel)jTable1.getModel();
        String Name=jComboBox1.getSelectedItem().toString()+" "+jTextField1.getText();
        
        String Gender="";
        if(jRadioButton1.isSelected()){
            Gender=jRadioButton1.getText();
        }
        else if(jRadioButton2.isSelected()){
            Gender=jRadioButton2.getText();
        }
        
        String dept="";
        if(jCheckBox1.isSelected())   
            dept=jCheckBox1.getText();  
        
        else if(jCheckBox2.isSelected())   
            dept=jCheckBox2.getText();  
        
        else if(jCheckBox3.isSelected())   
            dept=jCheckBox3.getText();  
        
        else if(jCheckBox4.isSelected())   
            dept=jCheckBox4.getText();  
        
        else if(jCheckBox5.isSelected())   
            dept=jCheckBox5.getText();  
        
        else if(jCheckBox6.isSelected())   
            dept=jCheckBox6.getText();  
               
        String ysem=jComboBox2.getSelectedItem().toString()+"/"+jComboBox3.getSelectedItem().toString();                    
        String Course_No=jList1.getSelectedValue().toString();
        String marks= jTextField4.getText();
        String grade= jTextField5.getText(); 
        
        model1.addRow(new Object[]{Name,Gender,dept,ysem,Course_No,marks,grade});
        
        
        try {
             
            //for overwrite
            /*FileWriter file = new FileWriter("output.txt");
             file.write(Name+" "+Gender+" "+dept+" "+ysem+" "+Course_No+" "+marks+" "+grade);
             file.close();
             JOptionPane.showMessageDialog(null, "Saved Successfully!");*/
                       
            //for new line           
             File file = new File("output.txt");
             FileWriter fw = new FileWriter(file,true);
             PrintWriter pw = new PrintWriter(fw);
             pw.println(Name+" "+Gender+" "+dept+" "+ysem+" "+Course_No+" "+marks+" "+grade);
             pw.close();
             JOptionPane.showMessageDialog(null, "Saved Successfully!");
            
         }
         catch (Exception e) {
             e.getStackTrace();
         }
    }//GEN-LAST:event_jButton1ActionPerformed

