    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        try {
            String s1 = jTextField4.getText();
            
            double num1 = Double.parseDouble(s1);
            if (num1 >= 80 && num1 <= 100) {
                jTextField5.setText("A+");
            } else if (num1 >= 75 && num1 <= 79) {
                jTextField5.setText("A");
            } else if (num1 >= 70 && num1 <= 74) {
                jTextField5.setText("A-");
            } else if (num1 >= 65 && num1 <= 69) {
                jTextField5.setText("B+");
            } else if (num1 >= 60 && num1 <= 64) {
                jTextField5.setText("B");
            } else if (num1 >= 55 && num1 <= 59) {
                jTextField5.setText("B-");
            } else if (num1 >= 50 && num1 <= 54) {
                jTextField5.setText("C+");
            } else if (num1 >= 45 && num1 <= 49) {
                jTextField5.setText("C");
            } else if (num1 >= 40 && num1 <= 44) {
                jTextField5.setText("D");
            } else if (num1 <= 39) {
                jTextField5.setText("F");
            }
        }catch(NumberFormatException n)
        {
            JOptionPane.showMessageDialog(null, "Enter Valid Marks!");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        DefaultTableModel model1=(DefaultTableModel)jTable1.getModel();
        
        try {
           File myObj = new File("output.txt");
           Scanner myReader = new Scanner(myObj);
           
           String[][] stu = new String[8][];
           int index = 0;
           
           while (myReader.hasNextLine() && index < stu.length) {
                 stu[index] = myReader.nextLine().split(" "); //split returns an array
                 index++;
           }
           for (String[] a : stu){             
                    model1.addRow(new Object[]{a[0]+" "+a[1],a[2],a[3],a[4],a[5],a[6],a[7]});              
           }
           
           myReader.close();
        } catch (Exception e) {
                         
        }
    }//GEN-LAST:event_jButton3ActionPerformed

